import Layout from './../components/Layout';
import Link from 'next/link';
import fetch from 'isomorphic-unfetch';

const colorStyle = {
    color: 'black'
};

let region;

const Index = (props) => {
    return (
    <Layout>
        <h1 style={colorStyle}>African Union Countries </h1>
        <ul style={colorStyle}>
            {props.region.map(({region}) => (
                <li style={colorStyle} key={region.numericCode}>
                    <Link style={colorStyle} as={`/i/${region.numericCode}`} href={`/info?numericCode=${region.numericCode}`}>
                        <a style={colorStyle}>{region.name}</a>
                    </Link>
                </li>
            ))}
        </ul>
        <style jsx>{`
            body {
                backgroundColor: 'black'
            }
        `}</style>
    </Layout>
)   
}

Index.getInitialProps = () => {
    fetch('https://restcountries.eu/rest/v2/region/africa')
    .then(res => res.json())
    .then(data => initialize(data))
    console.log(data);


   // console.log(`Show data fetched: ${data} `)

   /* return {
        region: data
    }
}*/
}
export default Index;
