import Layout from './../components/Layout';
import Link from 'next/link';
import fetch from 'isomorphic-unfetch';

const colorStyle = {
    color: 'white'
};

const Index = (props) => {
    return (
    <Layout>
        <h1 style={colorStyle}>African Union Countries </h1>
        <ul style={colorStyle}>
            {props.region.map(({region}) => (
                <li style={colorStyle} key={region.id}>
                    <Link style={colorStyle} as={`/i/${region.id}`} href={`/info?id=${region.name}`}>
                        <a style={colorStyle}>{region.name}</a>
                    </Link>
                </li>
            ))}
        </ul>
        <style jsx>{`
            body {
                backgroundColor: 'black'
            }
        `}</style>
    </Layout>
)   
}

Index.getInitialProps = async function() {
    const res = await fetch('https://restcountries.eu/rest/v2/region/africa');
    const data = await res.json()

    console.log(`Show data fetched. Count: ${data.length}`)

    return {
        region: data
    }
}

export default Index;
